glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform RET {
  float width_step;
  float height_step;
  float strength;
};
const float blur_rad = 3.0;
const float blur_pix = ( blur_rad * 2.0 + 1.0 ) * ( blur_rad * 2.0 + 1.0 );
void main ()
{
  vec4 texc = texture2D(texture, v_uv0);
  float olA = 0.0;
  for(float fy = -blur_rad; fy <= blur_rad; ++fy) {
    for(float fx = -blur_rad; fx <= blur_rad; ++fx) {
      vec2 coord = vec2(fx * width_step, fy * height_step);
      vec4 sample = texture2D(texture, v_uv0 + coord);
      olA += sample.a;
    }
  }
  olA = 1.0 - pow(1.0 - olA * 2.0 / blur_pix, strength);
  olA -= texc.a * 2.0;
  olA = clamp(olA, 0.0, 1.0);
  vec4 olC = vec4(0.4, 0.9, 0.4, 1.0);
  vec4 res = mix(texc, olC, olA);
  if(texc.a <= 0.0) {
    res = vec4(olC.rgb, olA);
  }
  gl_FragColor = res;
}

