glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform RET{
  vec4 color;
  float ctimer;
  float radius;
  float gtime;
};
void main ()
{
  const float radius = 5.0;
  vec4 accum = vec4(0.0);
  vec4 normal = vec4(0.0);
  normal = texture(texture, v_uv0);
  for(float i = 1.0; i <= radius; i += 1.0)
  {
      accum += texture(texture, vec2(v_uv0.x - 0.01 * i, v_uv0.y - 0.01 * i));
      accum += texture(texture, vec2(v_uv0.x + 0.01 * i, v_uv0.y - 0.01 * i));
      accum += texture(texture, vec2(v_uv0.x + 0.01 * i, v_uv0.y + 0.01 * i));
      accum += texture(texture, vec2(v_uv0.x - 0.01 * i, v_uv0.y + 0.01 * i));
  }
  accum.rgb =  color.rgb * color.a * accum.a;
  float opacity = ((1.0 - normal.a) / radius) * (sin(ctimer)/5.0 / gtime);
  normal = (accum * opacity) + (normal * normal.a);
  gl_FragColor = v_color * normal;
}

